class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>>mp;
        vector<int>ar;
        for (int i = 0; i < nums.size() / 2; i++) {
            int start = nums[i], end = nums[nums.size() - i - 1];
            int dif = abs(start-end);
            int m = max({start,end, k-start, k-end});
            mp[dif].push_back(m);
            ar.push_back(m);
        }
        int n = nums.size();
        sort(ar.begin(),ar.end());

        auto it = mp.begin();
        while (it != mp.end()) {
            int di = it->first;
            int zeta = lower_bound(ar.begin(),ar.end(), di)-ar.begin();
            zeta=zeta*2 + (nums.size() / 2 - zeta);

            for (auto& z: it->second) {
                zeta -= (z<di) ? 2 : 1;
            }

            n = min(n, zeta);
            ++it;
        }
       return n;
    }
};



