class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int cnt = 0, n = a.size(), maxf = 0, x = -1,ans=INT_MAX;
        unordered_map<int, int> f;

        for (int i = 0; i < n / 2; i++) {
            f[abs(a[i] - a[n - i - 1])]++;
        }
        for (const auto& [i, fr] : f) {
            if (maxf < fr) {
                x = i;
                maxf = fr;
            }
        }

        vector<int> tmp;
        if(n <= 1e4) {
            for (int i = 0; i < n / 2; i++) {
                tmp.push_back(abs(a[i]-a[n-i-1]));            
            }
        }
        else {
            for (const auto& [i, fr] : f) {
                if (maxf == fr) {
                    tmp.push_back(i); 
                }
            }            
            sort(tmp.begin(),tmp.end());
        }

        for(int j=0;j<tmp.size();j++) {
            x=tmp[j];
            for (int i = 0; i < n / 2; i++) {
                if(abs(a[i]-a[n-i-1]) != x) {
                    int g=max(a[i],a[n-i-1]),p=min(a[i],a[n-i-1]);
                    if(abs(a[i]-a[n-i-1]) > x) {                   
                        if(g-k<=x || k-p>=x) cnt++;
                        else if(k >= x) cnt+=2;
                        else {
                            cnt=0;
                            break;
                        }
                    }        
                    else {
                        if(k-p >= x || g >= x) cnt++;
                        else if(k>=x) cnt+=2;
                        else {
                            cnt=0;
                            break;
                        }
                    }
                }
            }
            ans=min(ans,cnt);
            cnt=0;
        }
        return ans;
    }
};
