class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        // multiset<int> ms;
        // int n=(int)nums.size();
        // for(int i=0;i<n/2;i++){
        //     ms.insert(i);
        //     ms.insert(i);
        // }
        // vector<pair<int,int>> v;
        // for(int i=0;i<n;i++)}{
        //     v.push_back(arr[i],i);
        // }
        // sort(v.begin(),v.end());
        int n=(int)nums.size();
        unordered_map<int,vector<int>> mp;
        for(int i=0;i<n/2;i++){
            int tmp=max(nums[i],max(nums[n-1-i],max(k-nums[i],k-nums[n-1-i])));
            mp[abs(nums[i]-nums[n-1-i])].push_back(tmp);
        }
        vector<int> v;
        for(auto &u:mp){
            for(auto &a:u.second){
                v.push_back(a);
            }
        }
        sort(v.begin(),v.end());
        int ans=n;
        for(auto &u:mp){
            int ind=lower_bound(v.begin(),v.end(),u.first)-v.begin();
            int tmp=n/2+ind;

            for(auto &a:u.second){
                tmp--;
                if(u.first-a>0){
                    tmp--;
                }
            }
            if(tmp<ans){
                ans=tmp;
            }
        }
        return ans;

    }
};
