class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
      int n = nums.size();
      vector<int> minn(n/2, 0);
      map<int, int> counts;
      map<int, int> freq;
      for(int i = 0; i<n/2; i++) {
        int a = nums[i];
        int b = nums[n - 1 - i];
        if(a > b)
          swap(a, b);

        minn[i] = max(b-0, k-a);
        counts[minn[i]]+=1;
        freq[b-a]++;
      }
      sort(minn.begin(), minn.end());

      int res = n*2;
      n = n/2;

      for(auto [val, count] : freq) {
        int l = upper_bound(minn.begin(), minn.end(), val-1) - minn.begin();
        int r = n - l - count;

        int curr_count = l*2 + r;
        res = min(curr_count, res);
      }
      return res;
    }
};
