class Solution {
public:
    int check(vector<int>& nums, int k,int maxi){
        int n=nums.size();
        int count=0;
        for(int i=0;i<n/2;i++){
            int temp=abs(nums[i]-nums[n-i-1]);
            if(temp==maxi)continue;
            int high=max(nums[i],nums[n-i-1]);
            int low=min(nums[i],nums[n-i-1]);
            if(high>=maxi || k-low >=maxi)count++;
            else count+=2;
        }
        return count;
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int>diff;
        int maxi=0;
        for(int i=0;i<n/2;i++){
            diff[abs(nums[i]-nums[n-i-1])]++;
            maxi=max(maxi,diff[abs(nums[i]-nums[n-i-1])]);
        }
        int mini=INT_MAX;
        for(auto it:diff){
            if(mini<=(n/2 - it.second))continue;
            mini=min(check(nums,k,it.first),mini);
        }
        return mini;
    }
};
