class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>mp;
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-1-i])]++;
        }
        vector<int>tmp;
        for(int i=0;i<n/2;i++){
            int m1=max(nums[i],k-nums[i]);
            int m2=max(nums[n-1-i],k-nums[n-1-i]);
            tmp.push_back(max(m1,m2));
        }
        vector<int>record(k+1);
        for(int x:tmp){
            record[x]++;
        }
        for(int i=k;i>0;i--){
            record[i-1]+=record[i];
        }
        int res=INT_MAX;
        for(int i=0;i<=k;i++){
            res=min(res,-record[i]+n-mp[i]);
        }
        return res;
    }
};
