class Solution {
public:
int minChanges(vector<int>& nums, int k) {
    map<int,int>help;
    int len=nums.size();
    for(int i=0;i<len/2;++i){
        int v1=min(nums[i],nums[len-1-i]);
        int v2=max(nums[i],nums[len-1-i]);
        help[0]++;
        help[v2-v1]--;
        help[v2-v1+1]++;
        int t=max(k-v1, v2);
        help[t+1]++;
    }
    int res=INT_MAX;
    int tmp=0;

    for(int i=0;i<=k;++i){
        tmp+=help[i];
        res=min(res,tmp);
    }
    return res;

}
};
