class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int> m;
        vector<int> val(100005,0);
        for(int i=0;i<n/2;i++){
            m[abs(nums[i]-nums[n-i-1])]++;
            int ma = max(nums[i],nums[n-i-1]);
            int mi = min(nums[i],nums[n-i-1]);
            int mini = min(mi,min(abs(ma-k),min(abs(mi-k),min(abs(ma-0),abs(mi-0)))));
            if(mi<=k) mini = 0;
            int maxi = max(mi,max(abs(ma-k),max(abs(mi-k),max(abs(ma-0),abs(mi-0)))));
            val[mini]++;
            val[maxi+1]--;
        }
        for(int i=1;i<=100003;i++)
        {
            val[i]=val[i]+val[i-1];
        }        
        int ans = 1e8;
        for(auto it:m){
            int req = val[it.first]-it.second;
            int req2 = n/2-req-it.second;
            int temp = req+2*req2;
            ans=min(ans,temp);
        }
        return ans;

    }
};
