class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        hash_map = defaultdict(list)
        lst = []
        n = len(nums)

        for i in range(n//2):
            a, b = nums[i], nums[n - i - 1]
            x = max([a, b, k - a, k - b])
            hash_map[abs(a - b)].append(x)
            lst.append(x)

        lst.sort()
        min_changes = n
        for diff, max_list in hash_map.items():
            lb = bisect_left(lst, diff)
            lb = lb * 2 + (n // 2 - lb)

            for max_val in max_list:
                if max_val < diff:
                    lb -= 2
                else:
                    lb -= 1
            min_changes = min(min_changes, lb)
        return min_changes
