#define deb(x) if(dc) cout<<" --> "<<(#x)<<" : "<<x<<endl;
#define deb2(x, y) if(dc) cout<<" --> "<<(#x)<<" : "<<x<<" , "<<(#y)<<" : "<<y<<endl;
#define debs(x) if(dc) cout<<"--> "<<x<<endl;

class Solution {
public:
typedef long long ll;
    int minChanges(vector<int>& arr, int k) {
        map<ll, ll> mp;
        vector<ll> brr;
        ll i = 0, j = arr.size() - 1;
        while(i<j){
            ll d = abs(arr[i] - arr[j]);
            mp[d] += 1;
            ll k1 = k - min(arr[i], arr[j]);
            ll k2 = max(arr[i], arr[j]);
            brr.push_back(max(k1, k2));
            i++;
            j--;
        }
        ll ans = INT_MAX;
        sort(brr.begin(),brr.end());
        print(brr);
        ll m = brr.size();
        deb(m);
        for(int i=0;i<=k;i++){
            deb(i);
            ll d1 = lower_bound(brr.begin(), brr.end(), i) - brr.begin();
            ll d2 = d1;
            deb(d1);
            d1--;
            deb(d2);
            ll res = 0;
            ll right = m - d2;
            deb(right);
            res += (right - mp[i]);
            deb2(res, d1);
            if(d1>=0){
                ll left = d1+1;
                deb(left);
                res += (left*2);
                deb(res);
            }
            ans = min(ans, res);
            deb(ans);
        }
        return ans;
    }
};
