class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        map<int, vector<int>>mp;
        vector<int>temp;
        for(int i = 0; i < n/2; i++){
            int a = nums[i];
            int b = nums[n-i-1];
            int d = abs(a-b);
            int temp1 = max({a, b, k-a, k-b});
            mp[d].push_back(temp1);
            temp.push_back(temp1);
        }
        sort(temp.begin(), temp.end());

        int ans = INT_MAX;
        for(auto it: mp){
            int temp1 = it.first;
            int lower = lower_bound(temp.begin(), temp.end(), temp1)-temp.begin();
            lower = lower*2 + (n/2-lower);

            for(auto i: it.second){
                if(i < temp1){
                    lower -= 2;                
                }
                else{
                    lower -= 1;
                }
            }
            ans = min(ans, lower);
        }

        return ans;













