class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        map<int, int> mp;
        mp[0] = -1;

        for (int i = 0; i < n / 2; i++) {
            mp[abs(nums[i] - nums[n - i - 1])]++;
        }

        int ans = 0, maxdiff = -1, maxi = INT_MIN;

        for (auto& x : mp) {
            if (x.second == n / 2) {
                return 0;
            }
            if (x.first <= k) {
                if (x.second > maxi) {
                    maxi = x.second;
                    maxdiff = x.first;
                }
            }
        }

        cout << maxdiff << endl;
        for (auto& x : mp) {
            cout << x.first << " " << x.second << endl;   
        }
        cout << "\n\n";

        map<int, vector<int>> sid_diffs;
        vector<int> sid_cdiff;

        for (int i = 0; i < n / 2; i++) {
            int sid_num1 = nums[i], sid_num2 = nums[n - i - 1], sid_curdiff = abs(sid_num1 - sid_num2);
            int sid_commondiff;
            sid_commondiff = max(sid_num1, max(sid_num2, max(k - sid_num1, k - sid_num2)));
            sid_diffs[sid_curdiff].push_back(sid_commondiff);
            sid_cdiff.push_back(sid_commondiff);

            sid_commondiff += -1;
        }

        ans = n;
        sort(sid_cdiff.begin(), sid_cdiff.end());

        cout << sid_cdiff[0] << endl;

        for (auto& x : sid_diffs) {
            int sid_curdiff = x.first;
            int sid_lb = lower_bound(sid_cdiff.begin(), sid_cdiff.end(), sid_curdiff) - sid_cdiff.begin();
            sid_lb = sid_lb * 2 + (n / 2 - sid_lb);

            for (auto& y : x.second) {
                if (y < sid_curdiff) {
                    sid_lb -= 2;
                } else {
                    sid_lb -= 1;
                }
            }

            ans = min(ans, sid_lb);
        }

        cout << ans << endl;

        return ans;
    }
};
