#define all(x) x.begin(), x.end()
#define right(i, a, b) for(int i=a; i<b; i++)
#define left(i, a, b) for(int i=a; i>=b; i--)
#define ain(arr,a,b) for(int i=(a); i<(b); i++) cin>>(arr)[i];
#define aout(arr,a,b) for(int i=(a); i<(b); i++) cout<<(arr)[i]<<" ";
typedef long long ll;
typedef long double ld;
typedef pair<int,int> pii;
typedef vector<int> vi;
typedef vector<pair<int,int>> vpii;
typedef vector<vector<int>> vvi;
typedef map<int,int> mii;
typedef set<int> si;
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
     map<int,vector<int>> m;
      int n=nums.size();
        vi v;
        right(i,0,n/2){
            int a = nums[i],b = nums[n-i-1];
            int diff = abs(a-b);
            int x = max({a,b,k-a,k-b});
            m[diff].pb(x);
            v.pb(x);
        }
        int ans = nums.size();
        sort(all(v));

        for(auto &val:m){

            int diff = val.f;
            int t = lower_bound(all(v),diff)-v.begin();
            t = t*2+(nums.size()/2-t);

            for(auto &x:val.s){
                if(x<diff){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            ans = min(ans,t);

        }

        return ans;    
    }
};
