class Solution {
public:
    int minChanges(vector<int> &nums, int k)
{
    map<int, vector<int>> diffToX;
    vector<int> xVals;
    int i = 0, j = nums.size() - 1;
    while (i < j)
    {
        int diff = abs(nums[i] - nums[j]);
        int X = max({nums[i], nums[j], k - nums[i], k - nums[j]});
        i++;
        j--;
        xVals.push_back(X);
        diffToX[diff].push_back(X);
    }
    int ans = 2 * nums.size();
    sort(xVals.begin(), xVals.end());

    for (auto &x : diffToX)
    {
        int temp = lower_bound(xVals.begin(), xVals.end(), x.first) - xVals.begin();
        temp = temp * 2 + (nums.size() / 2 - temp);

        for (auto &xx : x.second)
        {
            temp--;
            if (xx < x.first)
                temp--;
        }
        ans = min(ans, temp);
    }

    return ans;
}
};
