class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> vr;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            mp[diff].push_back(X);
            vr.push_back(X);
        }
        int ans = nums.size();
        sort(vr.begin(),vr.end());

        for(auto &val:mp){

            int diff = val.first;
            int T = lower_bound(vr.begin(),vr.end(),diff)-vr.begin();
            T = T*2+(nums.size()/2-T);

            for(auto &X:val.second){
                if(X<diff){
                    T-=2;
                }
                else{
                    T-=1;
                }
            }

            ans = min(ans,T);

        }

        return ans;

    }
};
