class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>ass(k+2,0);
        int n=nums.size();
        map<int,int>cnt;
        for(int i=0;i<=(n/2)-1;i++){

            int left=0;
            int right=abs(nums[i]-nums[n-i-1]);
            cnt[right]++;
            //i to 0
            right=max(right,abs(nums[n-i-1]));
            //i to k
            right=max(right,abs(k-nums[n-i-1]));
            //n-i-1 to 0
            right=max(right,abs(nums[i]));
            //n-i-1 to k
            right=max(right,abs(k-nums[i]));
            ass[left]++;
            ass[right+1]--;
        }
        int ans=INT_MAX; int sum=0;
        for(int i=0;i<ass.size();i++){
            sum+=ass[i];
            int temp=(1*sum)+(2*((n/2)-sum)) - cnt[i];
            ans=min(ans,temp); 

        }
        return ans;
    }
};
