#include <map>
#include <string>
#include <iostream>

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> diffMap;
        vector<int> maxValues;

        for (int index = 0; index < nums.size() / 2; ++index) {
            int first = nums[index];
            int second = nums[nums.size() - index - 1];
            int difference = abs(first - second);

            int maxValue = max({first, second, k - first, k - second});

            diffMap[difference].push_back(maxValue);
            maxValues.push_back(maxValue);
        }

        int minChangesRequired = nums.size();  
        sort(maxValues.begin(), maxValues.end());

        for (const auto& [difference, valueofthe] : diffMap) {
            int lowerBoundIndex = lower_bound(maxValues.begin(), maxValues.end(), difference) - maxValues.begin();
            int totalChanges = lowerBoundIndex * 2 + (nums.size() / 2 - lowerBoundIndex);

            for (const auto& value : valueofthe) {
                if (value < difference) {
                    totalChanges -= 2;
                } 
                else {
                    totalChanges -= 1;
                }
            }

            minChangesRequired = min(minChangesRequired, totalChanges);
        }

        return minChangesRequired;
    }
};
