class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp;
        for(int i=0;i<nums.size()/2;i++){
            mp[abs(nums[i]-nums[nums.size()-i-1])]++;
        }
        int maxi = -1;
        int diff = 1e9;
        priority_queue<pair<int,int>> pq;
        for(auto it:mp){
            pq.push({it.second,it.first});
            // cout<<it.first<<" "<<it.second<<endl;
            if(it.second>=maxi){
                if(it.second==maxi){
                    diff = min(it.first,diff);
                }else{
                    diff = it.first;
                }
                maxi = it.second;
            }
        }

        int temp_ans = 1e9;
        while(pq.size()!=0){
            int ans = 0;
            for(int i=0;i<nums.size()/2;i++){
                if(abs(nums[i]-nums[nums.size()-i-1])!=pq.top().second){
                    if(min(nums[i],nums[nums.size()-i-1])+pq.top().second<=k){
                        ans+=1;
                    }else if(max(nums[i],nums[nums.size()-i-1])-pq.top().second>=0){
                        ans+=1;
                    }else{
                        ans+=2;
                    }
                }
            }
            pq.pop();
            // cout<<ans<<" ";
            if(ans<=temp_ans){
                temp_ans = ans;
            }else{
                break;
            }
            // temp_ans = min(ans,temp_ans);
        }

        return temp_ans;
    }
};
