class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        diffs = defaultdict(int)
        for i in range(n // 2):
            diffs[abs(nums[i] - nums[n - i - 1])] += 1
        x = max(diffs.values())
        best = -1
        for most, c in diffs.items():
            minop = n // 2 - c
            if minop >= best and best != -1:
                continue
            res = 0
            for i in range(n // 2):
                d = abs(nums[i] - nums[n - i - 1])
                a = max(nums[i], nums[n - i - 1])
                b = min(nums[i], nums[n - i - 1])
                maxdiff = max(a, k - b)
                if d != most:
                    res += 1 if maxdiff >= most else 2
                    if res == best:
                        break
            if best == -1 or res < best:
                best = res

        return best
