class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ans=1e8;
        vector<int>&a=nums;
        int n=nums.size();
            int t=nums.size()/2;
            vector<int>f(nums.size(),0);
            struct node{
                int x,y;
                node(){}
                node(int xx,int yy):x(xx),y(yy){}
            };
            vector<node>v;
            map<int,int>mp;
            for(int i=0;i<t;i++){
                f[i]=max(max(a[i],a[n-i-1]),k-min(a[i],a[n-i-1]));
                //cout<<f[i]<<endl;
                int te=abs(a[i]-a[n-i-1]);
                v.push_back(node(abs(a[i]-a[n-i-1]),f[i]));
                if(f[i]>=te)mp[te]++;
                else mp[te]+=2;
            }
            vector<node>e;
            e=v;
            sort(v.begin(),v.end(),[](node A,node B){
                return A.y>B.y;
            });
            sort(e.begin(),e.end(),[](node A,node B){
                return A.x<B.x;
            });
            int now=0;
            int sum=e.size()*2;
            //cout<<mp[2]<<endl;
            for(int i=k;i>=0;i--){
                while(now<v.size()&&v[now].y>=i){
                    now++;
                    sum--;
                }
                //cout<<sum<<" "<<mp[i]<<endl;
                ans=min(ans,sum-mp[i]);
            }
        return ans;
    }
};
