class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> chirag;
        vector<int> agrawal;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            chirag[diff].push_back(X);
            agrawal.push_back(X);
        }
        int ans = nums.size();
        sort(agrawal.begin(),agrawal.end());

        for(auto &val:chirag){

            int diff = val.first;
            int temp = lower_bound(agrawal.begin(),agrawal.end(),diff)-agrawal.begin();
            temp = temp*2+(nums.size()/2-temp);

            for(auto &xyz:val.second){
                if(xyz<diff){
                    temp-=2;
                }
                else{
                    temp-=1;
                }
            }

            ans = min(ans,temp);

        }

        return ans;

    }
};
