class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> cnt;

        int i=0; 
        int j=nums.size()-1;

        while(i<j){
            int diff = abs(nums[i]-nums[j]);
            cnt[diff]++;
            i++;
            j--;
        }

        vector<vector<int>> map;

        for(auto it:cnt){
            map.push_back({it.second, it.first});
        }

        sort(map.begin(), map.end(), greater<vector<int>>());



        int minCnt = INT_MAX;
        int m = map.size();

        int size = min(m, 21);

        for(int idx = 0; idx<size; idx++){
            vector<int> it = map[idx];
            int i=0;
            int j=nums.size()-1;
            int major = it[1];
            int ans = 0;

            while(i<j){
            if(abs(nums[i]-nums[j]) == major){
                i++;
                j--;
            }
            else{
                if((nums[i]-major <= k && nums[i]-major >=0) 
                || (nums[i]+major <= k && nums[i]+major >= 0) 
                || (nums[j]-major <= k && nums[j]-major >= 0) 
                || (nums[j]+major <= k && nums[j]+major >= 0) ){
                    ans+=1;
                }
                else{
                    ans += 2;
                }

                i++;
                j--;
            }

        }
            minCnt = min(ans, minCnt);

        }

        return minCnt;
    }
};
