class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {
        map<int, vector<int>> m;
        vector<int> v;

        for (int i = 0; i < nums.size() / 2; i++) 
        {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

            int X = max({a, b, k - a, k - b});

            m[diff].push_back(X);
            v.push_back(X);
        }

        int min_changes = nums.size();  
        sort(v.begin(), v.end());

        for (const auto& [diff, values] : m) 
        {
            int curr = lower_bound(v.begin(), v.end(), diff) - v.begin();
            curr = curr * 2 + (nums.size() / 2 - curr);

            for (const auto& X : values) 
            {
                if (X < diff) {
                    curr -= 2;
                } else {
                    curr -= 1;
                }
            }

            min_changes = min(min_changes, curr);
        }

        return min_changes;
    }
};
