class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int res = n/2;
        vector<int> t(n/2, 0);
        for(int i = 0; i < n/2; i++) {
            t[i] = max(max(abs(k-nums[i]), nums[i]), max(abs(k-nums[n-i-1]), nums[n-i-1]));
        }
        sort(t.begin(), t.end());
        unordered_map<int, int> mp;
        for(int i = 0; i < n/2; i++) {
            mp[abs(nums[i]-nums[n-i-1])]++;
        }
        int move;
        for(auto [x, cnt] : mp) {
            auto it = lower_bound(t.begin(), t.end(), x);
            int a = it - t.begin();
            move = (n/2 - cnt - a) + a * 2;
            res = min(res, move);
        }
        return res;
    }
};
