class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> vp(k+5, 0);

        for(int i=0,j=n-1;i<j;i++,j--) {
            int x=abs(nums[i]-nums[j]);
            int r=max({abs(nums[i]), k-nums[i], nums[j], k-nums[j]});
            vp[0]+=2; vp[k+1]-=2;
            vp[0]--; vp[r+1]++;
            if (x<=r) {
                vp[x]--; vp[x+1]++;
            } else {
                vp[x]-=2; vp[x+1]+=2;
            }
        }
        int ans=n/2;
        for(int j=0;j<=k;j++) {
            if (j) vp[j]+=vp[j-1];
            mintt(ans, vp[j]);
        }
        return ans;
    }
};
