class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> diff(k + 1), maxdiff(k + 2);
        for (int i = 0; i < n / 2; i++) {
            int d = abs(nums[i] - nums[n - i - 1]);
            int m1 = max(nums[i], k - nums[i]);
            int m2 = max(nums[n - i - 1], k - nums[n - i - 1]);
            diff[d] ++;
            maxdiff[max(m1, m2)] ++;
        }
        // 只改变一次达到的最大差的组数
        for (int i = k - 1; i >= 0; i--) {
            maxdiff[i] += maxdiff[i + 1];
        }
        int ans = n;
        for (int i = 0; i <= k; i++) {
            ans = min(ans, n - maxdiff[i] - diff[i]);
        }
        return ans;
    }
};
