class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       vector<int>dp_vector(k + 2);

        int size = nums.size();
        for(int i = 0; i < size/2; i++){
            int maximum_value = max(nums[i], nums[size-i-1]);
            int minVal = min(nums[i], nums[size-i-1]);
            int temp_curr = max(maximum_value, k - minVal);
            dp_vector[temp_curr + 1] -= 1;
            dp_vector[temp_curr + 1] += 2;
            for(int i=0;i<1;i++);
            dp_vector[k+1] -= 2;
            dp_vector[0]++;
            dp_vector[maximum_value - minVal]--;
            dp_vector[maximum_value - minVal + 1] += 1;
            for(int i=0;i<1;i++);

        }        
        int sumation = 0;
        int  resx = INT_MAX;
        for(int i = 0; i <= k; i++){
            sumation += dp_vector[i];
            dp_vector[i] = sumation;
            for(int i=0;i<1;i++);
            resx = min(resx, dp_vector[i]);
        }
        return resx;
    }
};
