class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),ans=n;
        map<int,int> m;
        vector<int> count(k+1,0);
        for(int i=0;i<n/2;++i){
            if(nums[i]<nums[n-i-1]) swap(nums[i],nums[n-i-1]);
            m[nums[i]-nums[n-i-1]]++;
            count[max(nums[i],k-nums[n-i-1])]++;
        }
        for(int i=1;i<=k;++i){
            count[i]+=count[i-1];
            // cout<<count[i]<<';';
        }
        for(auto &[a,c]:m){
            ans=min(ans, (a?count[a-1]:0)+n/2-c);
        }
        return ans;
    }
};
