class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int> mp1,mp2;
        for(int i=0;i<n/2;i++){
            int temp = abs(nums[i]-nums[n-i-1]);
            mp1[temp]++;
            int maxm = max({nums[i],nums[n-i-1],abs(k-nums[i]),abs(k-nums[n-i-1])});
            if(maxm!=k){
                mp2[maxm+1]++;
            }
        }
        for(int i=1;i<=k;i++){
            mp2[i]+=mp2[i-1];
        }
        int minm = INT_MAX;
        for(int i=0;i<=k;i++){
            minm = min(minm,mp2[i]*2+n/2-mp1[i]-mp2[i]);
        }

        return minm;
    }
};
