class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size(), ans = n;
        map<int,int>m;
        for(int i=0;i<n/2;i++){
            m[abs(nums[i]-nums[n-i-1])]++;
        }
        vector<int>freq(k+2,0);
        freq[0] = n/2;
        for(int i=0;i<n/2;i++){
            int mx = max({nums[i],nums[n-i-1],k-min(nums[i],nums[n-i-1])});
            freq[mx+1]--;
        }
        ans = min(ans,freq[0]-m[0]+(n/2-freq[0])*2);
        for(int i=1;i<=k;i++){
            freq[i] += freq[i-1];
            ans = min(ans,freq[i]-m[i]+(n/2-freq[i])*2);
        }
        return ans;
    }
};
