class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n= nums.size();
        int i=0;
        int j= n-1;
        map<int,int> f;
        priority_queue<pair<int,int>> maxpq;
        while(i<j)
        {
            f[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }
        int k1=0;
        int ans=1e9;
        for(auto it: f)
        {
            maxpq.push({it.second,it.first});
        }
        while(maxpq.size()){
            auto it= maxpq.top();
            maxpq.pop();
            int d = it.second;
            int i=0;
            int j=n-1;
            int t = 0;
            while(i<j){
                int dif = abs(nums[i] - nums[j]);
                if(dif==d){
                    i++; j--;
                    continue;
                }
                int n1 = min(nums[i], nums[j]), n2 = max(nums[i], nums[j]);
                if(n2>=d || abs(k-n1)>=d) t++;
                else t+=2;
                i++;
                j--;
            }
            k1++;
            ans = min(ans, t);
            if(k1==10) break;
        }
        return ans;
    }
};
