class Solution {
public:
    int f(vector<int>& arr,int k,int diff){
        int n = arr.size();
        int cnt = 0;
        for(int i=0;i<n/2;i++){
            int mn = min(arr[i],arr[n-i-1]);
            int mx = max(arr[i],arr[n-i-1]);
            if(mx-mn==diff) continue;
            if(mx-mn>diff) cnt++;
            else{
                if(mn+diff<=k || mx-diff>=0) cnt++;
                else cnt += 2;
            }
        }
        return cnt;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int> mp;
        map<int,int> rmp1,rmp2;

        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
        }

        int ans = n;

        ans = min(ans,f(nums,k,mp.begin()->first));
        //cout << mp.begin()->first << " " << ans << endl;
        ans = min(ans,f(nums,k,mp.rbegin()->first));
        //cout << mp.rbegin()->first << " " << ans << endl;

        vector<int> temp;
        for(auto [x,y] : mp){
            rmp1[y] = max(rmp1[y],x);
            if(rmp2.count(y)==0) rmp2[y] = x;
            else rmp2[y] = min(rmp2[y],x);
            temp.push_back(x);
        }

        ans = min(ans,f(nums,k,rmp1.begin()->second));
        //cout << rmp1.begin()->second << " " << ans << endl;
        ans = min(ans,f(nums,k,rmp1.rbegin()->second));
        //cout << rmp1.rbegin()->second << " " << ans << endl;

        ans = min(ans,f(nums,k,rmp2.begin()->second));
        //cout << rmp2.begin()->second << " " << ans << endl;
        ans = min(ans,f(nums,k,rmp2.rbegin()->second));
        //cout << rmp2.rbegin()->second << " " << ans << endl;

        int m = temp.size();

        ans = min(ans,f(nums,k,temp[m/2]));
        if(m%2==0 && m/2+1<m){
            ans = min(ans,f(nums,k,temp[m/2+1]));
        }

        return ans;


//         for(int i=0;i<=k;i++){
//             cout << i << " ";
//             cout << f(nums,k,i) << endl;
//         }

//         return n;
    }

};
