class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> vec;
        for(int i=0;i<nums.size()/2;i++){
            int x=nums[i];
            int y=nums[nums.size()-i-1];
            int difference = abs(x-y);

            int X = max({x,y,k-x,k-y});
            mp[difference].push_back(X);
            vec.push_back(X);
        }
        int result=nums.size();
        sort(vec.begin(), vec.end());
        for(const auto &[difference, values]:mp){
            int Test=lower_bound(vec.begin(), vec.end(),difference)-vec.begin();
            Test=Test*2 + (nums.size()/2 - Test);

            for(const auto&X: values){
                if(X<difference){
                    Test-=2;
                }
                else{
                    Test-=1;
                }
            }
            result=min(result,Test);  
        }
        return result;
    }
};
