class Solution {
public:
  int minChanges(vector<int>& A, int k) {
    int n = A.size();
    unordered_map<int,vector<int>> mp;
    vector<int> v;
    for (int i=0; i<n/2; ++i) {
      int x = A[i];
      int y = A[n-1-i];
      int diff = abs(x-y);
      int z = max({x,y,k-x,k-y});
      v.push_back(z);
      mp[diff].push_back(z);
    }
    int ans = n;
    sort(v.begin(), v.end());
    for (auto& p : mp) {
      int diff = p.first;
      int x = lower_bound(v.begin(),v.end(),diff)-v.begin();
      x = x*2+(n/2-x);
      for (auto& a : p.second) {
        if(a<diff) x-=2;
        else x-=1;
      }
      ans = min(ans,x);
    }
    return ans;
  }
};
