class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> d;
        vector<int> m;
        for (int i = 0; i < nums.size() / 2; i++) {
            int x = max({nums[i], nums[nums.size() - i - 1], k - nums[i], k - nums[nums.size() - i - 1]});
            int y = abs(nums[i] - nums[nums.size() - i - 1]);
            d[y].push_back(x);
            m.push_back(x);
        }
         sort(m.begin(), m.end());
        int r = nums.size();


        for (auto &e : d) {
            int y = e.first;
            int p = lower_bound(m.begin(), m.end(), y) - m.begin();
            int c = p * 2 + (nums.size() / 2 - p);

            for (auto &v : e.second) {
                if(v>=y) {
                    c -= 1;
                }
                 else {
                    c -= 2;
                }
            }

            r = min(r, c);
        }

        return r;
    }
};
