class Solution {
public:
    int minChanges(vi& nums, int k) {
        map<int, vi> m;
        vi v;

        int n = nums.size();
        for (int i = 0; i < n / 2; ++i) {

            int df = abs(nums[i] - nums[n - i - 1]);

            int X = max({nums[i], nums[n - i - 1], k - nums[i], k - nums[n - i - 1]});

            m[df].psb(X);
            v.psb(X);
        }

        int sol = n;  
        srt(v);

        for (auto& [df, vls] : m) {
            int T = lower_bound(v.begin(), v.end(), df) - v.begin();
            T = T * 2 + (n / 2 - T);

            for (const auto& X : vls) {
                if (X < df) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            sol = min(sol, T);
        }

        return sol;
    }
};
