class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        vector<int>v1;
        int n=nums.size();
        int res=n; 
        map<int,vector<int>>mp;

        for (int i=0;i<n/2;i++) 
        {

            int y=nums[n-i-1];
            int x=nums[i];
            int diff=abs(x-y);
            int maxi=max(max(x,y),max(k-x,k-y));
             v1.push_back(maxi);
             mp[diff].push_back(maxi);

        }


        sort(v1.rbegin(), v1.rend());
        reverse(v1.begin(),v1.end());

        for (auto x:mp) 
        {

            int len=lower_bound(v1.begin(), v1.end(), x.first)-v1.begin();
            len=(nums.size()/2-len)+2*len;

            for (auto y: x.second)
            {
                if(y>=x.first)
                {
                    len-=1;
                }
               else if (y<x.first)
                {
                    len-=2;
                }


            }

            res=min(res, len);
        }

        return res;
    }
};
