class Solution {
public:
    int solver(vector<int> &nums, int dif, int k){
        int ans=0;
        // cout << n << endl;
        // cout << l << " " << r << endl;
        int l=0, r=nums.size()-1;

        while(l<r){

            if(abs(nums[l]-nums[r])!=dif){
                if(nums[l]<dif && nums[r]<dif){
                    if(abs(k-nums[l])>=dif || abs(k-nums[r])>=dif) ans++;
                    else{
                        // cout << nums[l] << " " << nums[r] << endl;
                        ans+=2;
                    }
                } else{
                    ans++;
                }
            }
            l++;
            r--;
            // cout << ans << " ";
        }

        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        map<int, int> mp;
        int n=nums.size();

        int l=0, r=n-1;

        while(l<r){
            mp[abs(nums[l]-nums[r])]++;
            l++;
            r--;
        }

        int dif=INT_MAX, fre=INT_MIN, dif2=INT_MAX;

        for(auto it: mp){
            // cout << it.first << " "<< it.second << endl;
            if(it.second>fre){
                fre=it.second;
                dif2=dif;
                dif=it.first;
            }
        }


        l=0, r=n-1;
        // cout << dif << " "<< dif2 << endl;

        // cout << dif << " " << fre << endl;

        int ans=solver(nums, dif, k);
        if(dif2!=INT_MAX){
            ans = min(ans, solver(nums, dif2, k));
        }

        return ans;
    }
};
