class Solution {
public:

    void mark(int a,int b,int k,vector<int> &pre){
        int maxi=max(max(a,b),k-min(a,b));
        pre[0]++;
        pre[maxi+1]--;
    }

    int minChanges(vector<int>& arr, int k) {
        vector<int> orig(k+1,0);
        vector<int> pre(k+2,0);

        int i=0,j=arr.size()-1;
        while(i<j){
            int diff=abs(arr[i]-arr[j]);
            orig[diff]++;
            mark(arr[i],arr[j],k,pre);
            i++;
            j--;
        }

        for(int i=1;i<pre.size();i++){
            pre[i]+=pre[i-1];
        }

        int ans=INT_MAX;
        for(int i=0;i<=k;i++){
            int n=arr.size()/2;
            int rem=n-pre[i];
            int curr=pre[i]-orig[i]+(2*rem);
            ans=min(ans,curr);
        }

        return ans;
    }
};
