func minChanges(nums []int, k int) int {
    n := len(nums)
    res := n * 2
    diff := make(map[int]int, n * 2)
    maxDiff := make([]int, 0, n / 2)
    for x, y := 0, n - 1; x < y; x, y = x + 1, y - 1 {
        a, b := nums[x], nums[y]
        if a > b { a, b = b, a }
        diff[b - a]++
        maxDiff = append(maxDiff, max(k - a, b))
    }
    sort.Ints(maxDiff)
    for x := 0; x <= k; x++ {
        cnt := 0
        idx := lowerBound(maxDiff, x)
        if idx == 0 {
            cnt = len(maxDiff) - diff[x]
        } else if idx == len(maxDiff) {
            cnt = len(maxDiff) * 2
        } else {
            cnt = idx * 2 + len(maxDiff) - idx - diff[x]
        }
        res = min(res, cnt)
    }
    return res
}

func lowerBound(arr []int, v int) int {
    l, r, m := 0, len(arr) - 1, 0
    for l <= r {
        m = (l + r) / 2
        if arr[m] >= v {
            r = m - 1
        } else {
            l = m + 1
        }
    }
    return l
} 

