#include <vector>
#include <map>
#include <algorithm>
#include <cmath>
#include <limits>

class Solution {
    public:
    int reverse(int x) {
        long r=0;      // decleare r 
        while(x){
         r=r*10+x%10; // find remainder and add its to r
         x=x/10;     // Update the value of x
        }
        if(r>INT_MAX || r<INT_MIN) return 0; // check range if r is outside the range then return 0  
        return int(r);  // if r in the 32 bit range then return r
    }
public:
    int minChanges(std::vector<int>& nums, int k) {
        std::map<int, std::vector<int>> m;
        std::vector<int> v;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = std::abs(a - b);

             int X = std::max({a, b, k - a, k - b});

             m[diff].push_back(X);
            v.push_back(X);
        }

        int ans = nums.size();  
        std::sort(v.begin(), v.end());

        for (const auto& [diff, values] : m) {
            int T = std::lower_bound(v.begin(), v.end(), diff) - v.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = std::min(ans, T);
        }

        return ans;
    }
};
