class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
        map<int,vector<int>> mpp;
        int n=arr.size();
        vector<int> d;
        for(int i = 0;i<n/2;i++){
            int a = arr[i],b = arr[n-i-1];
            int dif = abs(a-b);
            int x = max({a,b,k-a,k-b});
            mpp[dif].push_back(x);
            d.push_back(x);
        }
        int ans = n;
        sort(d.begin(),d.end());

        for(auto &it:mpp){

            int dif = it.first;
            int t = lower_bound(d.begin(),d.end(),dif)-d.begin();
            t = t*2+(n/2-t);

            for(auto &x:it.second){
                if(x<dif){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            ans = min(ans,t);

        }

        return ans;

    }
};
