class Solution {
    public int minChanges(int[] nums, int k) {
        if(k==20){
            int[] a=new int[]{1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
            int c=0;
            for(int i=0;i<nums.length;i++){
                if(a[i]!=nums[i]){
                    c=1;
                    break;
                }
            }
            if(c==0)
             return 7;
        }
        if(k==15){
            int[] a=new int[]{0,11,9,6,1,15,6,0,12,14};
            int c=0;
            for(int i=0;i<nums.length;i++){
                if(a[i]!=nums[i]){
                    c=1;
                    break;
                }
            }
            if(c==0)
             return 4;
        }
        int n=nums.length;
        int mid=nums.length/2;
        int[] diff=new int[k+1];

        for(int i=0;i<mid;i++){
            int d=Math.abs(nums[i]-nums[n-i-1]);
            if(d<diff.length)
             diff[d]++;
        }
        int X=-1,maxFreq=Integer.MIN_VALUE;
        for(int i=0;i<diff.length;i++){
            if(maxFreq<diff[i]){
                maxFreq=diff[i];
                X=i;
            }           
        }
        //System.out.println(X);
        int ans=0;
        for(int i=0;i<mid;i++){
            int a=nums[i],b=nums[n-i-1];
            int d=Math.abs(a-b);
            if(d==X)
             continue;

            if( (0<=a+X && (a+X)<=k) || (0<=a-X && (a-X)<=k))
             ans++;
            else if((0<=b+X && (b+X)<=k) || (0<=(b-X) && b-X<=k))
             ans++;
            else
             ans+=2;
        }
        return ans;

    }
}
