class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>cnt(k+1);
        int n=nums.size();
        for(int i=0;i<(nums.size())/2;i++){
            cnt[abs(nums[i]-nums[n-1-i])]++;
        }
        int x=max_element(cnt.begin(),cnt.end())-cnt.begin();
        int res1=n/2;
        int l=-1;int r=1e9;int cn=0;
        // cout<<x<<endl;
        for(int i=0;i<(nums.size())/2;i++){
            int templ=0;
            int tempr=max(max(nums[i],nums[n-i-1]),k-min(nums[i],nums[n-i-1])) ;
            r=min(r,tempr);
            if(abs(nums[i]-nums[n-i-1])!=x){
                cn++;
                if(tempr<x )cn++;
            }
            // cnt[abs(nums[i]-nums[n-1-i])]++;
        }
        res1=min(res1,cn);
        int nw=0;int le=0;
        for(int i=0;i<=r;i++){
            if(nw<cnt[i]){
                nw=cnt[i];le=i;
            }
        }
        // cout<<r<<endl;
        int de=0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1]) ==le )continue;
            int tempr=max(max(nums[i],nums[n-i-1]),k-min(nums[i],nums[n-i-1])) ;
            if(tempr<le)de++;
            de++;

        }
        // cout<<de<<endl;
        return min(de,res1);


    }
};
