class Solution:
 def minChanges(self, nums: List[int], k: int) -> int:
    diff_map = {}
    max_vals = []

    for i in range(len(nums) // 2):
        a, b = nums[i], nums[len(nums) - i - 1]
        diff = abs(a - b)
        max_val = max(a, b, k - a, k - b)

        if diff not in diff_map:
            diff_map[diff] = []
        diff_map[diff].append(max_val)
        max_vals.append(max_val)

    min_changes = len(nums)
    max_vals.sort()

    for diff, values in diff_map.items():
        total_changes = bisect.bisect_left(max_vals, diff) * 2 + (len(nums) // 2 - bisect.bisect_left(max_vals, diff))

        for val in values:
            if val < diff:
                total_changes -= 2
            else:
                total_changes -= 1

        min_changes = min(min_changes, total_changes)

    return min_changes
