class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int half = n>>1;
        vector<int> range(half);
        // vector<int> minus(half);
        unordered_map<int, int> cnt;
        for (int i = 0; i < half; ++i) {
            int a = min(nums[i], nums[n-i-1]);
            int b = max(nums[i], nums[n-i-1]);
            range[i] = max(b, k-a);
            cnt[b-a]++;
        }
        sort(range.begin(), range.end()); // 从小到大排序
        // sort(minus.begin(), minus.end());
        int ret = INT_MAX;
        // for (auto i: range) {
        //     cout << i << " ";
        // }
        // cout << endl;
        for (int i = 0; i <= k; ++i) {
            // 二分查找 range 中有多少个数小于等于 i
            int l = 0, r = half;
            while (l < r) {
                int m = l + ((r-l)>>1);
                if (range[m] < i) l = m+1;
                else r = m;
            }
            int cnt1 = l;
            int cnt2 = half - cnt1;
            int xxx = (cnt1<<1) + cnt2 - cnt[i];
            // cout << i << " " << l << " " << xxx << endl;
            ret = min(ret, xxx);
        }
        return ret;
    }
};
