class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int i=0, j=n-1;
        vector<int> d(k+1,0);
        vector<int> c(k+1,0);
        while(i<j) {
            int diff = (abs(nums[i] - nums[j]));
            c[diff]++;
            int x = max(nums[i], nums[j]);
            int y = min(nums[i], nums[j]);
            if (k-x > y) {
                d[k-y]++;
            } else {
                d[x]++;
            }
            i++;
            j--;
        }

        for(int a=1; a<=k; a++) {
            d[a] += d[a-1];
        }

        n /= 2;

        int ans = INT_MAX;
        for(int a=0; a<=k; a++) {
            int cur = 0;
            int y = n - c[a];
            if (a>0) {
                int x = d[a-1];
                cur += 2*x;
                y -= x;
            }
            cur += y;
            ans = min(ans, cur);
        }

        return ans;
    }
};
