#include <bits/stdc++.h>
using namespace std;
#define siddhantnema ios_base::sync_with_stdio(0); cin.tie(0);
#define ll long long
#define inparr(arr, N) for (ll i = 0; i < N; i++) { cin >> arr[i]; }
#define forn(_i, N) for (ll _i = 0; _i < N; _i++)
#define trc(x) cout << #x << " -> " << x << '\n';
#define len(x) (ll)x.size()

class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {
        map<int,int> ok;
        int MX = 0;
        int diff = -1;
        int N = len(nums);
        vector<int> vec;
        forn(i,N/2)
        {
            ok[abs(nums[i]-nums[N-1-i])]++;
            vec.push_back(max(k-min(nums[i],nums[N-1-i]),max(nums[i],nums[N-1-i])));
        }

        int res = N/2;
        sort(vec.begin(),vec.end());


        forn(i,k+1)
        {
            auto iter = lower_bound(vec.begin(),vec.end(),i) - vec.begin();
            int tmp = iter*2 + N/2 - iter - ok[i];

            res = min(res,tmp);
        }

        return res;   
    }
};
