class Solution {
public:

    int help(vector<int>& nums, int n, int k, int x)
    {
        int ans = 0;

        for(int i = 0; i<n/2; i++)
        {
            int d = abs(nums[i] - nums[n-i-1]);
            if(d != x)
            {
                int mn = min(nums[i],  nums[n-i-1]), mx = max(nums[i],  nums[n-i-1]);
                int diff = abs(d-x);
                if(d > x)
                {
                    if(mn + diff <= k || mx - diff >= 0)    ans++;
                    else ans +=2;
                }

                else
                {
                    if(mx + diff <= k || mn - diff >= 0)    ans++;
                    else ans +=2;
                }
            }
        }
        // cout<<x<<" "<<ans<<endl;

        return ans;
    }

    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> m;
        for(int i = 0; i<n/2; i++)
            m[abs(nums[i] - nums[n-i-1])]++;
        int mx = 0;
        vector<int> v;
        int ans = 1e9;
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> q;

        for(auto i: m)
        {
            if(q.size() < 10)   q.push({i.second, i.first});
            else if(i.second > q.top().first)
            {
                q.pop();
                q.push({i.second, i.first});
            }
        }

        while(!q.empty())
        {
            int i = q.top().second;
            q.pop();
            ans = min(ans, help(nums, n, k, i));
        }
        return ans;
    }
};
