class Solution {

    public int minChanges(int[] nums, int k) {
        int n = nums.length;


        Map<Integer, Integer> map = new HashMap<Integer, Integer>();
        int max = 0;
        for(int i = 0; i < n/2; i++) {
            int diff = Math.abs(nums[i]-nums[n-i-1]);
            int before = map.getOrDefault(diff, 0);
            map.put(diff, before+1);
            max = Math.max(max, before+1);
            // System.out.println(nums[i] + " " + nums[n-i-1] );
        }

        if ( map.size() == 1 ) return 0;

        // System.out.println(map + " " + max);

        Iterator<Integer> it = map.keySet().iterator();

        List<int[]> list = new ArrayList<int[]>();

        int result = Integer.MAX_VALUE;
        while(it.hasNext()){
            int checkdiff = it.next();
            int cnt = map.get(checkdiff);
            list.add(new int[]{checkdiff, cnt});


        }

        Collections.sort(list, (a,b)->b[1]-a[1]);

        // for(int i = 0; i < list.size(); i++) System.out.print("["+ list.get(i)[0] + " " + list.get(i)[1]+"]"); System.out.println();

        for(int a = 0; a < list.size(); a++) {

            int checkdiff = list.get(a)[0];
            int cnt = list.get(a)[1];


            if ( n/2 - cnt >= result ) continue;

                int changecnt = 0;
                for(int i = 0; i < n/2; i++) {
                    int diff = Math.abs(nums[i]-nums[n-i-1]);
                    if ( checkdiff == diff ) continue;
                    else {
                        if ( checkdiff + nums[i] <= k  || checkdiff +nums[n-i-1] <= k || nums[n-i-1]-checkdiff >= 0 || nums[i] -checkdiff >=0 ) changecnt++;
                        else changecnt += 2;

                        // System.out.println(nums[i]+ " " + nums[n-i-1] + " " + changecnt ) ;
                    }
                }
                result= Math.min(result, changecnt);
        }




        return result;
    }
}
